INSERT INTO `products` VALUES (512,'Viên sủi bổ sung vitamin C tăng đề kháng Swiss Energy (Tuýp 20 viên)','vien-sui-bo-sung-vitamin-c-tang-de-khang-swiss-energy-tuyp-20-vien-1606649771','SKUP12810',1,295,'<p>Swiss Energy Vitamin C 1000mg bổ sung vitamin C n&acirc;ng cao sức đề kh&aacute;ng v&agrave; thể trạng cho cơ thể. Sản phẩm c&oacute; vị cam v&agrave; dễ sử dụng.</p>',NULL,'160664977120201129183611mBTNMXhoRAohYGJNDaLuexRpnv5MYw0FgVDU7DB8.jpeg',99.00,104.00,104.00,98,1,6,109,1,1,1,0,'2023-11-29 18:36:11','2023-10-19 17:58:35'),(513,'Thực phẩm bảo vệ sức khỏe viên uống bổ sung vitamin C Blackmores Bio C 1000mg (Hộp 31 viên)','thuc-pham-bao-ve-suc-khoe-vien-uong-bo-sung-vitamin-c-blackmores-bio-c-1000mg-hop-31-vien-1606649948','SKUP14460',1,243,'<p>Thực phẩm bảo vệ sức khỏe Blackmores Bio C 1000mg bổ sung Vitamin C cho cơ thể, kh&ocirc;ng c&oacute; chất l&agrave;m ngọt nh&acirc;n tạo, ph&ugrave; hợp cho cả gia đ&igrave;nh. Sản phẩm chứa bioflavonoid gi&uacute;p tăng sự hấp thu vitamin C trong cơ thể, cung cấp tối đa Vitamin C chống cảm c&uacute;m, tăng sức đề kh&aacute;ng v&agrave; chữa l&agrave;nh vết thương một c&aacute;ch nhanh ch&oacute;ng.</p>',NULL,'160664994820201129183908tXPyOD112p0bDto0aWkA5bic9F76wOPw7O0J9E79.jpeg',300.00,313.00,313.00,99,1,1,106,1,1,1,0,'2023-11-29 18:39:08','2023-10-20 00:02:17'),(514,'Viên bổ sung Vitamin C Nat C 1000 (30 viên/lọ)','vien-bo-sung-vitamin-c-nat-c-1000-30-vienlo-1606650329','SKUP04376',1,296,'<p>Thực phẩm bảo vệ sức khỏe NAT C 1000. Cung cấp vitamin C cho cơ thể. Hỗ trợ giảm nguy cơ cảm lạnh, cảm cúm và tăng cường sức đề kháng.</p>',NULL,'160665032920201129184529GxAfhCg9nwrNfzT7y2PORt4XfVjmNJxqhliJGlcx.jpeg',90.00,108.00,97.20,100,1,20,13,1,1,1,0,'2023-11-29 18:45:29','2023-10-19 19:26:02'),(515,'Thực phẩm bảo vệ sức khỏe cung cấp vitamin C, tăng cường sức đề kháng Nat C Ester 1000mg (Hộp 30 gói)','thuc-pham-bao-ve-suc-khoe-cung-cap-vitamin-c-tang-cuong-suc-de-khang-nat-c-ester-1000mg-h